copy.body:
- name: ${1}
  copy:
    src: ${2}
    dest: ${3}
    ower: root
    group: root
    mode: '0644'

file.body:
- name: ${1}
  file:
    path: ${2}
    owner: root${3}
    group: root${4}
    mode: '0644${0}'

fileinfile.body:
- name: ${1}
  lineinfile:
   path: ${2}
   regexp: '^SELINUX='${3}
   line: ${4}
   create: yes${5}

git.body:
- name: ${1}
  git:
   repo:${2}
   dest: ${3}
   version: ${3}
   accept_hostkey: yes
   force: yes

cron.body:
- name: ${1}
  cron:
    name: ${2}
    minute: ${3}
    hour: ${4}
    day: ${5}
    month: ${6}
    job: ${7}
    user: ${8}
